const lat = 38.873568;
const long = -77.118037;

function calculateSunrise(date, latitude, longitude) {
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();

    // Calculate day of year
    const N1 = Math.floor(275 * month / 9);
    const N2 = Math.floor((month + 9) / 12);
    const N3 = (1 + Math.floor((year - 4 * Math.floor(year / 4) + 2) / 3));
    const N = N1 - (N2 * N3) + day - 30;

    // Convert longitude to hour value and calculate approximate time
    const lngHour = longitude / 15;
    const t = N + ((6 - lngHour) / 24);

    // Calculate sun's mean anomaly
    const M = (0.9856 * t) - 3.289;

    // Calculate sun's true longitude
    let L = M + (1.916 * Math.sin(M * Math.PI / 180)) + (0.020 * Math.sin(2 * M * Math.PI / 180)) + 282.634;
    L = L % 360;
    if (L < 0) L += 360;

    // Calculate sun's right ascension
    let RA = Math.atan(0.91764 * Math.tan(L * Math.PI / 180)) * 180 / Math.PI;
    RA = RA % 360;
    if (RA < 0) RA += 360;

    // Right ascension value needs to be in the same quadrant as L
    const Lquadrant = (Math.floor(L / 90)) * 90;
    const RAquadrant = (Math.floor(RA / 90)) * 90;
    RA = RA + (Lquadrant - RAquadrant);
    RA = RA / 15;

    // Calculate sun's declination
    const sinDec = 0.39782 * Math.sin(L * Math.PI / 180);
    const cosDec = Math.cos(Math.asin(sinDec));

    // Calculate sun's local hour angle
    const cosH = (Math.cos(90.833 * Math.PI / 180) - (sinDec * Math.sin(latitude * Math.PI / 180))) / (cosDec * Math.cos(latitude * Math.PI / 180));

    if (cosH > 1 || cosH < -1) {
        // Sun never rises or sets at this location on this date
        return new Date(year, month - 1, day, 6, 0, 0); // Default to 6 AM
    }

    // Calculate local mean time of rising
    const H = (360 - Math.acos(cosH) * 180 / Math.PI) / 15;

    const T = H + RA - (0.06571 * t) - 6.622;

    // Adjust back to UTC
    let UT = T - lngHour;
    UT = UT % 24;
    if (UT < 0) UT += 24;

    const now = new Date();
    const timezoneOffsetHours = -now.getTimezoneOffset() / 60;
    let localTime = UT + timezoneOffsetHours;
    if (localTime < 0) localTime += 24;
    if (localTime >= 24) localTime -= 24;

    const hours = Math.floor(localTime);
    const minutes = Math.floor((localTime - hours) * 60);

    return new Date(year, month - 1, day, hours, minutes, 0);
}

